
require 'lolcat/lol'

# Monkeypatch to read lines not files
module Lol
  def self.cat(lines, opts={})
    lines.each do |line|
      opts[:os] += 1
      println(line, opts)
    end
    ensure
  end
end

module Ohai
  class Kitten

    def self.the_kitten_must_live
      opts = {
        :animate => false,
        :duration => 12,
        :os => 0,
        :speed => 20,
        :spread => 8.0,
        :freq => 0.3
      }
      Lol.cat(kitten, opts)
    end

    private

    def self.kitten
      <<-KITTEN.split("\n")
